// Dashboard.js
import React, { useState, useEffect, useCallback } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Bar, Line, Pie } from "react-chartjs-2";
import DeviceCard from "./DeviceCard";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const REFRESH_OPTIONS = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1m", value: 60000 },
  { label: "5m", value: 300000 },
  { label: "10m", value: 600000 },
  { label: "30m", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user, fetchData }) {
  const [allDevices, setAllDevices] = useState([]);
  const [visibleDevices, setVisibleDevices] = useState({});
  const [timeRange, setTimeRange] = useState("day");
  const [customRange, setCustomRange] = useState([null, null]);
  const [refreshInterval, setRefreshInterval] = useState(5000);

  const [startDate, endDate] = customRange;

  // Toggle device visibility
  const handleToggleDevice = (id) => {
    setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  // Fetch devices periodically
  useEffect(() => {
    if (!fetchData) return;
    const fetchAndUpdate = async () => {
      const data = await fetchData();
      const colored = data.map((d) => ({
        ...d,
        kWh: Number(d.kWh ?? 0),
        color: d.color || "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setAllDevices(colored);

      // Initialize visibleDevices if empty
      if (Object.keys(visibleDevices).length === 0) {
        const vis = {};
        colored.forEach((d) => (vis[d.DEVICE_ID] = true));
        setVisibleDevices(vis);
      }
    };
    fetchAndUpdate();
    const interval = setInterval(fetchAndUpdate, refreshInterval);
    return () => clearInterval(interval);
  }, [fetchData, refreshInterval]);

  // Filter devices by time range
  const filteredDevices = allDevices.filter((d) => {
    const date = new Date(d.CREATE_AT || d.lastUpdate || new Date());
    const now = new Date();
    if (timeRange === "day") return date.toDateString() === now.toDateString();
    if (timeRange === "week") {
      const weekStart = new Date(now);
      weekStart.setDate(now.getDate() - now.getDay());
      return date >= weekStart && date <= now;
    }
    if (timeRange === "month") {
      return (
        date.getMonth() === now.getMonth() &&
        date.getFullYear() === now.getFullYear()
      );
    }
    if (timeRange === "custom" && startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      end.setHours(23, 59, 59, 999);
      return date >= start && date <= end;
    }
    return true;
  });

  // Devices selected for chart
  const chartDevices = filteredDevices.filter((d) => visibleDevices[d.DEVICE_ID]);

  const totalKwh = chartDevices.reduce((sum, d) => sum + d.kWh, 0);

  const chartData = {
    labels: chartDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: chartDevices.map((d) => d.kWh),
        backgroundColor: chartDevices.map((d) => d.color),
        borderColor: chartDevices.map((d) => d.color),
        borderWidth: 2,
      },
    ],
  };

  const lineChartData = {
    labels: chartDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: chartDevices.map((d) => d.kWh),
        borderColor: chartDevices.map((d) => d.color),
        borderWidth: 3,
        fill: false,
        tension: 0.4,
      },
    ],
  };

  const pieData = {
    labels: chartDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        data: chartDevices.map((d) => d.kWh),
        backgroundColor: chartDevices.map((d) => d.color),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const d = chartDevices[context.dataIndex];
            if (!d) return "";
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${
              d.STATUS || ""
            }`;
          },
        },
      },
      legend: { display: false },
    },
    scales: { y: { beginAtZero: true } },
  };

  return (
    <div style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}>
      {/* Filters */}
      <div style={{ display: "flex", gap: 16, flexWrap: "wrap", alignItems: "center" }}>
        <div>
          <label>Time Range:</label>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            style={{ marginLeft: 8, padding: "4px 8px" }}
          >
            <option value="day">Hôm nay</option>
            <option value="week">Tuần này</option>
            <option value="month">Tháng này</option>
            <option value="custom">Tùy chỉnh</option>
          </select>
        </div>
        {timeRange === "custom" && (
          <DatePicker
            selectsRange
            startDate={startDate}
            endDate={endDate}
            onChange={(update) => setCustomRange(update)}
            isClearable
            placeholderText="Chọn khoảng ngày"
          />
        )}
        <div>
          <label>Refresh:</label>
          <select
            value={refreshInterval}
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
            style={{ marginLeft: 8, padding: "4px 8px" }}
          >
            {REFRESH_OPTIONS.map((o) => (
              <option key={o.value} value={o.value}>{o.label}</option>
            ))}
          </select>
        </div>
        <button onClick={fetchData} style={{ padding: "4px 8px" }}>Refresh Now</button>
      </div>

      {/* Device toggle above charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 8 }}>
        {allDevices.map((d) => (
          <button
            key={d.DEVICE_ID}
            onClick={() => handleToggleDevice(d.DEVICE_ID)}
            style={{
              padding: "6px 12px",
              borderRadius: 8,
              border: visibleDevices[d.DEVICE_ID] ? `2px solid ${d.color}` : "1px solid #ccc",
              backgroundColor: visibleDevices[d.DEVICE_ID] ? d.color : "#fff",
              color: visibleDevices[d.DEVICE_ID] ? "#fff" : "#000",
              cursor: "pointer",
              transition: "0.2s",
            }}
          >
            {d.DEVICE_NAME}
          </button>
        ))}
      </div>

      {/* Total energy */}
      <div style={{ backgroundColor: "#fff", padding: 16, borderRadius: 12, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>{totalKwh} kWh</p>
      </div>

      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ đường</h4>
          <Line data={lineChartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 300px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ tròn</h4>
          <Pie data={pieData} options={{ ...chartOptions, maintainAspectRatio: true }} />
        </div>
      </div>

      {/* Device info cards */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {filteredDevices.map((d) => (
          <DeviceCard key={d.DEVICE_ID} device={d} />
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
