import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend);

export default function Dashboard({ devices = [] }) {
  const totalKwh = devices.reduce((sum, d) => sum + Number(d.kWh ?? 0), 0);

  const chartData = {
    labels: devices.map(d => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: devices.map(d => Number(d.kWh ?? 0)),
        backgroundColor: devices.map(d => d.color),
        borderColor: devices.map(d => d.color),
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const d = devices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${d.STATUS || ""}`;
          },
        },
      },
      legend: { display: false },
    },
    scales: { y: { beginAtZero: true } },
  };

  return (
    <div style={{ padding: 16 }}>
      <h3>Tổng năng lượng: {totalKwh} kWh</h3>
      <div style={{ display: "flex", gap: 24, flexWrap: "wrap" }}>
        <div style={{ flex: 1, minWidth: 300 }}>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: 1, minWidth: 300 }}>
          <Line data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: 1, minWidth: 300 }}>
          <Pie data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
}
