// Dashboard.js
import React, { useState, useEffect, useRef } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const REFRESH_OPTIONS = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1m", value: 60000 },
  { label: "5m", value: 300000 },
  { label: "10m", value: 600000 },
  { label: "30m", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user }) {
  const [devices, setDevices] = useState([]);
  const [timeRange, setTimeRange] = useState("today");
  const [customRange, setCustomRange] = useState([null, null]);
  const [refreshInterval, setRefreshInterval] = useState(5000); // default 5s
  const intervalRef = useRef(null);

  // Mặc định load hôm nay
  useEffect(() => {
    const today = new Date();
    setCustomRange([today, today]);
  }, []);

  // Fetch devices
  const fetchDevices = async () => {
    if (!user?.USER_ID) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      const data = await res.json();
      const colored = data.map((d) => ({
        ...d,
        kWh: Number(d.kWh ?? 0),
        color:
          d.color ||
          "#" +
            ((d.DEVICE_ID * 1234567) % 0xffffff).toString(16).padStart(6, "0"),
      }));
      setDevices(colored);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  // Fetch lần đầu và theo interval
  useEffect(() => {
  const fetchDevices = async () => {
    if (!user?.USER_ID) return;
    try {
      const res = await fetch(`http://localhost:5000/api/devices/user/${user.USER_ID}`);
      const data = await res.json();
      const colored = data.map((d) => ({
        ...d,
        kWh: Number(d.kWh ?? 0),
        color:
          d.color || "#" + ((d.DEVICE_ID * 1234567) % 0xffffff).toString(16).padStart(6, "0"),
      }));
      setDevices(colored);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  fetchDevices();
  if (intervalRef.current) clearInterval(intervalRef.current);
  intervalRef.current = setInterval(fetchDevices, refreshInterval);

  return () => clearInterval(intervalRef.current);
}, [user, refreshInterval]);


  // Filter devices theo thời gian
  const filteredDevices = devices.filter((d) => {
    const deviceDate = new Date(d.lastUpdate || d.CREATE_AT || new Date());
    const [start, end] = customRange;
    if (timeRange === "today") {
      const today = new Date();
      return deviceDate.toDateString() === today.toDateString();
    }
    if (timeRange === "yesterday") {
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);
      return deviceDate.toDateString() === yesterday.toDateString();
    }
    if (timeRange === "tomorrow") {
      const tomorrow = new Date();
      tomorrow.setDate(tomorrow.getDate() + 1);
      return deviceDate.toDateString() === tomorrow.toDateString();
    }
    if (timeRange === "custom" && start && end) {
      const startTime = new Date(start);
      startTime.setHours(0, 0, 0, 0);
      const endTime = new Date(end);
      endTime.setHours(23, 59, 59, 999);
      return deviceDate >= startTime && deviceDate <= endTime;
    }
    return true;
  });

  const totalKwh = filteredDevices.reduce((sum, d) => sum + d.kWh, 0);

  // Chart data
  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => d.kWh),
        backgroundColor: filteredDevices.map((d) => d.color),
        borderColor: filteredDevices.map((d) => d.color),
        borderWidth: 2,
      },
    ],
  };

  return (
    <div
      style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}
    >
      {/* Time & Refresh controls */}
      <div
        style={{
          display: "flex",
          gap: 16,
          alignItems: "center",
          flexWrap: "wrap",
        }}
      >
        <div>
          <label>Time: </label>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
          >
            <option value="yesterday">Hôm qua</option>
            <option value="today">Hôm nay</option>
            <option value="tomorrow">Ngày mai</option>
            <option value="custom">Tùy chỉnh</option>
          </select>
        </div>
        {timeRange === "custom" && (
          <DatePicker
            selectsRange
            startDate={customRange[0]}
            endDate={customRange[1]}
            onChange={(update) => setCustomRange(update)}
            isClearable
          />
        )}
        <div>
          <label>Refresh: </label>
          <select
            value={refreshInterval}
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
          >
            {REFRESH_OPTIONS.map((o) => (
              <option key={o.value} value={o.value}>
                {o.label}
              </option>
            ))}
          </select>
        </div>
        <button onClick={fetchDevices}>Refresh Now</button>
      </div>

      {/* Total kWh */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 16,
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          minWidth: 200,
        }}
      >
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>
      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={{ responsive: true }} />
        </div>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ đường</h4>
          <Line data={chartData} options={{ responsive: true }} />
        </div>
        <div
          style={{
            flex: "1 1 300px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <Pie data={chartData} options={{ responsive: true }} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
