import React, { useState } from "react";
import axios from "axios";

export default function DeviceManager({ user, devices, onDevicesUpdate }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("ON");

  const handleAddDevice = async (e) => {
    e.preventDefault();
    if (!deviceName || !address) return alert("Thiếu thông tin device");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: deviceName,
        ADDRESS: address,
        STATUS: status,
        USER_ID: Number(user.USER_ID),
      });

      if (onDevicesUpdate) {
        onDevicesUpdate(prev => [...prev, {...res.data, color:"#"+Math.floor(Math.random()*16777215).toString(16), kWh:0}]);
      }

      setDeviceName(""); setAddress(""); setStatus("ON");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Lỗi khi thêm device");
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Device Manager</h2>
      <form onSubmit={handleAddDevice} style={{ marginBottom:16 }}>
        <input type="text" placeholder="Device Name" value={deviceName} onChange={e=>setDeviceName(e.target.value)} style={{ marginRight:8 }} />
        <input type="text" placeholder="Address" value={address} onChange={e=>setAddress(e.target.value)} style={{ marginRight:8 }} />
        <select value={status} onChange={e=>setStatus(e.target.value)} style={{ marginRight:8 }}>
          <option value="ON">ON</option>
          <option value="OFF">OFF</option>
        </select>
        <button type="submit">Add Device</button>
      </form>

      <ul>
        {devices.map(d => (
          <li key={d.DEVICE_ID}>{d.DEVICE_NAME} | {d.kWh} kWh | {d.STATUS}</li>
        ))}
      </ul>
    </div>
  );
}
