import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import DeviceManager from "./components/DeviceManager";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [devices, setDevices] = useState([]);
  const [activeMenu, setActiveMenu] = useState("Dashboard");

  const handleLogout = () => {
    setUser(null);
    setDevices([]);
  };

  useEffect(() => {
    const fetchDevices = async () => {
      if (!user?.USER_ID) return;
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices/user/${user.USER_ID}`
        );
        const data = await res.json();
        const colored = data.map((d) => ({
          ...d,
          kWh: Number(d.kWh ?? 0), // ép kiểu number, null hoặc undefined thành 0
          color:
            d.color || "#" + Math.floor(Math.random() * 16777215).toString(16), // gán màu ngẫu nhiên nếu chưa có
        }));
        setDevices(colored);
      } catch (err) {
        console.error("❌ Error fetching devices:", err);
      }
    };
    fetchDevices();
  }, [user]);

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = (
        <DeviceManager
          user={user}
          devices={devices}
          onDevicesUpdate={setDevices}
        />
      );
      break;
    case "Setting":
      content = <Setting />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container">
      <Sidebar
        activeMenu={activeMenu}
        setActiveMenu={setActiveMenu}
        user={user}
        onLogout={handleLogout}
      />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
