import React, { useState } from "react";
import axios from "axios";

export default function DeviceManager({ user, onDevicesUpdate }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("ON");

  const handleAddDevice = async (e) => {
    e.preventDefault();
    if (!deviceName || !address) return alert("Thiếu thông tin device");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: deviceName,
        ADDRESS: address,
        STATUS: status,
        USER_ID: Number(user.USER_ID),
      });
      console.log("Device added:", res.data);
      // Reload devices
      if (onDevicesUpdate) onDevicesUpdate((prev) => [...prev, res.data]);
      // Clear form
      setDeviceName("");
      setAddress("");
      setStatus("ON");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Lỗi khi thêm device");
    }
  };

  return (
    <div>
      <h2>Device Manager</h2>
      <form onSubmit={handleAddDevice}>
        <input
          type="text"
          placeholder="Device Name"
          value={deviceName}
          onChange={(e) => setDeviceName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="ON">ON</option>
          <option value="OFF">OFF</option>
        </select>
        <button type="submit">Add Device</button>
      </form>

      <ul>
        {user?.devices?.map((d) => (
          <li key={d.DEVICE_ID}>
            {d.DEVICE_NAME} | {d.kWh} kWh | {d.STATUS}
          </li>
        ))}
      </ul>
    </div>
  );
}
