import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend);

function Dashboard({ devices = [] }) {
  const devicesWithColors = devices.map((d, idx) => ({
    ...d,
    color: d.color || "#" + Math.floor(Math.random() * 16777215).toString(16)
  }));

  const totalKwh = devicesWithColors.reduce((sum, d) => sum + Number(d.kWh || 0), 0);

  const chartData = {
    labels: devicesWithColors.map(d => d.DEVICE_NAME),
    datasets: [{
      label: "kWh",
      data: devicesWithColors.map(d => Number(d.kWh || 0)),
      backgroundColor: devicesWithColors.map(d => d.color),
      borderColor: devicesWithColors.map(d => d.color),
      borderWidth: 2
    }]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: context => {
            const d = devicesWithColors[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${d.STATUS || ""}`;
          }
        }
      },
      legend: { display: false }
    },
    scales: { y: { beginAtZero: true } }
  };

  return (
    <div style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}>
      <div style={{ backgroundColor:"#fff", padding:16, borderRadius:12, boxShadow:"0 4px 12px rgba(0,0,0,0.1)", minWidth:200 }}>
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin:0 }}>{totalKwh} kWh</p>
      </div>

      <div style={{ display:"flex", flexWrap:"wrap", gap:16 }}>
        {devicesWithColors.map(d => (
          <div key={d.DEVICE_ID} style={{ flex:"1 1 220px", backgroundColor:"#fff", borderRadius:12, boxShadow:"0 4px 12px rgba(0,0,0,0.1)", padding:16, borderLeft:`6px solid ${d.color}` }}>
            <h4>{d.DEVICE_NAME}</h4>
            <p><strong>Địa chỉ:</strong> {d.ADDRESS}</p>
            <p><strong>Status:</strong> <span style={{color:d.STATUS==="ON"?"#4caf50":"#f44336"}}>{d.STATUS}</span></p>
            <p><strong>kWh:</strong> {d.kWh}</p>
          </div>
        ))}
      </div>

      <div style={{ display:"flex", flexWrap:"wrap", gap:24 }}>
        <div style={{ flex:"1 1 400px", height:300, backgroundColor:"#fff", borderRadius:12, padding:16, boxShadow:"0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex:"1 1 400px", height:300, backgroundColor:"#fff", borderRadius:12, padding:16, boxShadow:"0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ đường</h4>
          <Line data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex:"1 1 300px", height:300, backgroundColor:"#fff", borderRadius:12, padding:16, boxShadow:"0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ tròn</h4>
          <Pie data={chartData} options={{...chartOptions, maintainAspectRatio:true}} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
