import React, { useState } from "react";

function DeviceCard({ device, onUpdate, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    kWh: device.kWh,
    STATUS: device.STATUS,
    ADDRESS: device.ADDRESS,
  });

  const handleSave = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/devices/${device.DEVICE_ID}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(editData),
        }
      );
      const updatedDevice = await res.json();
      onUpdate(updatedDevice);
      setIsEditing(false);
    } catch (err) {
      console.error(err);
      alert("C·∫≠p nh·∫≠t th·∫•t b·∫°i");
    }
  };

  return (
    <div
      style={{
        padding: 15,
        background: "#fff",
        borderRadius: 8,
        boxShadow: "0 0 5px rgba(0,0,0,0.1)",
        width: 250,
        margin: 10,
        position: "relative",
      }}
    >
      <h4>{device.DEVICE_NAME}</h4>
      {!isEditing ? (
        <>
          <p>kWh: {device.kWh}</p>
          <p>Status: {device.STATUS}</p>
          <p>Address: {device.ADDRESS}</p>
        </>
      ) : (
        <div style={{ display: "flex", flexDirection: "column", gap: 4 }}>
          <input
            type="number"
            value={editData.kWh}
            onChange={(e) =>
              setEditData({ ...editData, kWh: parseFloat(e.target.value) })
            }
            min="0"
            step="0.01"
          />
          <select
            value={editData.STATUS}
            onChange={(e) =>
              setEditData({ ...editData, STATUS: e.target.value })
            }
          >
            <option value="On">On</option>
            <option value="Off">Off</option>
          </select>
          <input
            type="text"
            value={editData.ADDRESS}
            onChange={(e) =>
              setEditData({ ...editData, ADDRESS: e.target.value })
            }
          />
        </div>
      )}
      <div
        style={{
          position: "absolute",
          top: 5,
          right: 5,
          display: "flex",
          gap: 5,
        }}
      >
        {!isEditing ? (
          <>
            <button onClick={() => setIsEditing(true)}>‚úèÔ∏è</button>
            <button onClick={onDelete}>‚ùå</button>
          </>
        ) : (
          <>
            <button onClick={handleSave}>üíæ</button>
            <button onClick={() => setIsEditing(false)}>‚ùå</button>
          </>
        )}
      </div>
    </div>
  );
}

export default DeviceCard;
