import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import DeviceManager from "./components/DeviceManager";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [devices, setDevices] = useState([]);
  const [activeMenu, setActiveMenu] = useState("Dashboard");

  // Dashboard state
  const [timeRange, setTimeRange] = useState("today");
  const [customRange, setCustomRange] = useState([null, null]);
  const [refreshInterval, setRefreshInterval] = useState(5000);

  // Fetch devices
  const fetchDevices = async () => {
    if (!user?.USER_ID) return;
    try {
      const res = await fetch(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      const data = await res.json();
      const colored = data.map((d) => ({
        ...d,
        kWh: Number(d.kWh ?? 0),
        color:
          d.color ||
          "#" +
            ((d.DEVICE_ID * 1234567) % 0xffffff).toString(16).padStart(6, "0"),
      }));
      setDevices(colored);
    } catch (err) {
      console.error("❌ Error fetching devices:", err);
    }
  };

  // Auto fetch devices according to interval
  useEffect(() => {
    fetchDevices();
    const interval = setInterval(fetchDevices, refreshInterval);
    return () => clearInterval(interval);
  }, [user, refreshInterval]);

  const handleLogout = () => {
    setUser(null);
    setDevices([]);
  };

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = (
        <Dashboard
          devices={devices}
          timeRange={timeRange}
          setTimeRange={setTimeRange}
          customRange={customRange}
          setCustomRange={setCustomRange}
          refreshInterval={refreshInterval}
          setRefreshInterval={setRefreshInterval}
          fetchDevices={fetchDevices}
        />
      );
      break;
    case "Device":
      content = (
        <DeviceManager
          user={user}
          devices={devices}
          onDevicesUpdate={setDevices}
        />
      );
      break;
    case "Setting":
      content = <Setting />;
      break;
    default:
      content = (
        <Dashboard
          devices={devices}
          timeRange={timeRange}
          setTimeRange={setTimeRange}
          customRange={customRange}
          setCustomRange={setCustomRange}
          refreshInterval={refreshInterval}
          setRefreshInterval={setRefreshInterval}
          fetchDevices={fetchDevices}
        />
      );
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container">
      <Sidebar
        activeMenu={activeMenu}
        setActiveMenu={setActiveMenu}
        user={user}
        onLogout={handleLogout}
      />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
