import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const timeOptions = [
  { label: "Hôm qua", value: "yesterday" },
  { label: "Hôm nay", value: "today" },
  { label: "Ngày mai", value: "tomorrow" },
];

const refreshOptions = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1p", value: 60000 },
  { label: "5p", value: 300000 },
  { label: "10p", value: 600000 },
  { label: "30p", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user }) {
  const [devices, setDevices] = useState([]);
  const [timeFilter, setTimeFilter] = useState("today");
  const [refreshInterval, setRefreshInterval] = useState(5000);
  const intervalRef = useRef(null);

  const fetchDevices = async () => {
    try {
      if (!user?.USER_ID) return;
      const res = await axios.get(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      setDevices(res.data);
    } catch (err) {
      console.error("❌ Error fetching devices:", err);
    }
  };

  // Fetch when component mounts or timeFilter changes
  useEffect(() => {
    fetchDevices();
  }, [timeFilter, user]);

  // Auto-refresh logic
  useEffect(() => {
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(fetchDevices, refreshInterval);
    return () => clearInterval(intervalRef.current);
  }, [refreshInterval, timeFilter, user]);

  return (
    <div style={{ padding: 16 }}>
      <h2>Dashboard</h2>

      {/* Filter */}
      <div style={{ display: "flex", gap: 16, marginBottom: 16 }}>
        <select
          value={timeFilter}
          onChange={(e) => setTimeFilter(e.target.value)}
        >
          {timeOptions.map((opt) => (
            <option key={opt.value} value={opt.value}>
              {opt.label}
            </option>
          ))}
        </select>

        <select
          value={refreshInterval}
          onChange={(e) => setRefreshInterval(Number(e.target.value))}
        >
          {refreshOptions.map((opt) => (
            <option key={opt.value} value={opt.value}>
              {opt.label}
            </option>
          ))}
        </select>

        <button onClick={fetchDevices}>Refresh ngay</button>
      </div>

      {/* Devices list */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              flex: "1 1 220px",
              backgroundColor: "#fff",
              borderRadius: 12,
              padding: 16,
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>
              <strong>Địa chỉ:</strong> {d.ADDRESS}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                style={{ color: d.STATUS === "ON" ? "#4caf50" : "#f44336" }}
              >
                {d.STATUS}
              </span>
            </p>
            <p>
              <strong>kWh:</strong> {d.kWh}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
