// Dashboard.js
import React, { useState, useEffect, useRef } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const REFRESH_OPTIONS = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1m", value: 60000 },
  { label: "5m", value: 300000 },
  { label: "10m", value: 600000 },
  { label: "30m", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user, devices, setDevices, dashboardState, setDashboardState, fetchData }) {
  const { timeRange, customRange, refreshInterval } = dashboardState;
  const [startDate, endDate] = customRange;
  const intervalRef = useRef(null);

  // Fetch ngay khi mount
  useEffect(() => {
    if (!fetchData || !user?.USER_ID) return;

    const loadDevices = async () => {
      const newDevices = await fetchData(user.USER_ID);
      setDevices(newDevices);
    };

    loadDevices(); // fetch ngay

    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(loadDevices, refreshInterval);

    return () => clearInterval(intervalRef.current);
  }, [user, refreshInterval, fetchData, setDevices]);

  // Filter devices theo timeRange / customRange
  const filteredDevices = devices.filter((d) => {
    const now = new Date();
    const deviceDate = new Date(d.lastUpdate || d.CREATE_AT || now);

    if (timeRange === "day") return deviceDate.toDateString() === now.toDateString();
    if (timeRange === "week") {
      const weekStart = new Date(now);
      weekStart.setDate(now.getDate() - now.getDay());
      return deviceDate >= weekStart && deviceDate <= now;
    }
    if (timeRange === "month") {
      return deviceDate.getMonth() === now.getMonth() && deviceDate.getFullYear() === now.getFullYear();
    }
    if (timeRange === "custom" && startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      end.setHours(23, 59, 59, 999);
      return deviceDate >= start && deviceDate <= end;
    }
    return true;
  });

  const totalKwh = filteredDevices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);

  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const d = filteredDevices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${d.STATUS || ""}`;
          },
        },
      },
      legend: { display: false },
    },
    scales: { y: { beginAtZero: true } },
  };

  // Update dashboardState khi thay đổi
  const handleTimeRangeChange = (value) => setDashboardState({ ...dashboardState, timeRange: value });
  const handleCustomRangeChange = (range) => setDashboardState({ ...dashboardState, customRange: range });
  const handleRefreshIntervalChange = (value) => setDashboardState({ ...dashboardState, refreshInterval: value });

  return (
    <div style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}>
      {/* Filters */}
      <div style={{ display: "flex", gap: 16, alignItems: "center" }}>
        <div>
          <label>Time Range: </label>
          <select value={timeRange} onChange={(e) => handleTimeRangeChange(e.target.value)}>
            <option value="day">Hôm nay</option>
            <option value="week">Tuần này</option>
            <option value="month">Tháng này</option>
            <option value="custom">Tùy chỉnh</option>
          </select>
        </div>
        {timeRange === "custom" && (
          <DatePicker
            selectsRange
            startDate={startDate}
            endDate={endDate}
            onChange={handleCustomRangeChange}
            isClearable
            placeholderText="Chọn khoảng ngày"
          />
        )}
        <div>
          <label>Refresh: </label>
          <select value={refreshInterval} onChange={(e) => handleRefreshIntervalChange(Number(e.target.value))}>
            {REFRESH_OPTIONS.map((o) => (
              <option key={o.value} value={o.value}>{o.label}</option>
            ))}
          </select>
        </div>
        <button onClick={() => fetchData(user.USER_ID).then(setDevices)}>Refresh Now</button>
      </div>

      {/* Total kWh */}
      <div style={{ backgroundColor: "#fff", padding: 16, borderRadius: 12, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>{totalKwh} kWh</p>
      </div>

      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ đường</h4>
          <Line data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 300px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ tròn</h4>
          <Pie data={chartData} options={{ ...chartOptions, maintainAspectRatio: true }} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
