// Dashboard.js
import React, { useState, useEffect, useRef } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const REFRESH_OPTIONS = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1m", value: 60000 },
  { label: "5m", value: 300000 },
  { label: "10m", value: 600000 },
  { label: "30m", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user, devices, setDevices, dashboardState, setDashboardState, fetchData }) {
  const { timeRange, customRange, refreshInterval } = dashboardState;
  const [localCustomRange, setLocalCustomRange] = useState(customRange);
  const intervalRef = useRef(null);

  useEffect(() => {
    const today = new Date();
    setLocalCustomRange([today, today]);
  }, []);

  const fetchDevices = async () => {
    if (!user?.USER_ID) return;
    const data = await fetchData(user.USER_ID);
    setDevices(data);
  };

  useEffect(() => {
    fetchDevices();
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(fetchDevices, refreshInterval);
    return () => clearInterval(intervalRef.current);
  }, [user, refreshInterval]);

  // --- Filter devices chuẩn SQL Server ---
  const filteredDevices = devices.filter((d) => {
    let deviceDate;
    if (d.lastUpdate) {
      deviceDate = new Date(d.lastUpdate.replace(" ", "T"));
    } else if (d.CREATE_AT) {
      deviceDate = new Date(d.CREATE_AT.replace(" ", "T"));
    } else {
      deviceDate = new Date();
    }
    deviceDate.setHours(0, 0, 0, 0);

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (timeRange === "today") return deviceDate.getTime() === today.getTime();

    if (timeRange === "yesterday") {
      const yesterday = new Date(today);
      yesterday.setDate(yesterday.getDate() - 1);
      yesterday.setHours(0, 0, 0, 0);
      return deviceDate.getTime() === yesterday.getTime();
    }

    if (timeRange === "custom" && localCustomRange[0] && localCustomRange[1]) {
      const start = new Date(localCustomRange[0]);
      start.setHours(0, 0, 0, 0);
      const end = new Date(localCustomRange[1]);
      end.setHours(23, 59, 59, 999);
      return deviceDate >= start && deviceDate <= end;
    }

    return true;
  });

  const totalKwh = filteredDevices.reduce((sum, d) => sum + d.kWh, 0);

  // Chart cột & line
  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        data: filteredDevices.map((d) => d.kWh),
        backgroundColor: "#3b82f6",
        borderColor: "#3b82f6",
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { display: false } },
  };

  // Pie chart chỉ tổng kWh
  const pieData = {
    labels: ["Tổng năng lượng"],
    datasets: [
      { data: [totalKwh], backgroundColor: ["#8b5cf6"] },
    ],
  };

  return (
    <div style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}>
      {/* Header + Controls */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap" }}>
        <h2 style={{ margin: 0 }}>IECMS</h2>
        <div style={{ display: "flex", gap: 16, alignItems: "center", flexWrap: "wrap" }}>
          <div>
            <label>Time: </label>
            <select value={timeRange} onChange={(e) => setDashboardState(prev => ({ ...prev, timeRange: e.target.value }))}>
              <option value="yesterday">Hôm qua</option>
              <option value="today">Hôm nay</option>
              <option value="custom">Tùy chỉnh</option>
            </select>
          </div>
          {timeRange === "custom" && (
            <DatePicker
              selectsRange
              startDate={localCustomRange[0]}
              endDate={localCustomRange[1]}
              onChange={(update) => {
                setLocalCustomRange(update);
                setDashboardState(prev => ({ ...prev, customRange: update }));
              }}
              isClearable
            />
          )}
          <div>
            <label>Refresh: </label>
            <select
              value={refreshInterval}
              onChange={(e) => setDashboardState(prev => ({ ...prev, refreshInterval: Number(e.target.value) }))}
            >
              {REFRESH_OPTIONS.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
            </select>
          </div>
          <button onClick={fetchDevices}>Refresh Now</button>
        </div>
      </div>

      {/* Tổng kWh */}
      <div style={{ backgroundColor: "#fff", padding: 16, borderRadius: 12, boxShadow: "0 4px 12px rgba(0,0,0,0.1)", minWidth: 200 }}>
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>{totalKwh} kWh</p>
      </div>

      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 400px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ đường</h4>
          <Line data={chartData} options={chartOptions} />
        </div>
        <div style={{ flex: "1 1 300px", height: 300, backgroundColor: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
          <h4>Biểu đồ tròn (Tổng)</h4>
          <Pie data={pieData} options={{ responsive: true, plugins: { legend: { display: false } } }} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
