import React, { useState } from "react";
import axios from "axios";

export default function DeviceManager({ user, devices, onDevicesUpdate }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("ON");
  const [kWh, setKWh] = useState("");

  const handleAddDevice = async (e) => {
    e.preventDefault();
    if (!deviceName || !address || !kWh) {
      return alert("Thi·∫øu th√¥ng tin device");
    }

    // üîπ Debug: log tr∆∞·ªõc khi g·ª≠i
    console.log("Sending new device:", {
      DEVICE_NAME: deviceName,
      ADDRESS: address,
      STATUS: status,
      kWh: Number(kWh),
      USER_ID: Number(user.USER_ID),
    });

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: deviceName,
        ADDRESS: address,
        STATUS: status,
        kWh: Number(kWh),
        USER_ID: Number(user.USER_ID),
      });

      // üîπ Debug: log response t·ª´ backend
      console.log("Backend response:", res.data);

      // C·∫≠p nh·∫≠t state devices
      if (onDevicesUpdate) onDevicesUpdate((prev) => [...prev, res.data]);

      // Reset form
      setDeviceName("");
      setAddress("");
      setStatus("ON");
      setKWh("");
    } catch (err) {
      console.error("‚ùå Error adding device:", err);
      alert(err.response?.data?.error || "L·ªói khi th√™m device");
    }
  };

  return (
    <div>
      <h2>Device Manager</h2>

      <form
        onSubmit={handleAddDevice}
        style={{ display: "flex", gap: 8, flexWrap: "wrap", marginBottom: 16 }}
      >
        <input
          type="text"
          placeholder="Device Name"
          value={deviceName}
          onChange={(e) => setDeviceName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <input
          type="number"
          placeholder="kWh"
          value={kWh}
          onChange={(e) => setKWh(e.target.value)}
          min="0"
          step="0.01"
        />
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="ON">ON</option>
          <option value="OFF">OFF</option>
        </select>
        <button type="submit">Add Device</button>
      </form>

      <h3>Device List</h3>
      <ul>
        {devices?.map((d) => (
          <li key={d.DEVICE_ID}>
            {d.DEVICE_NAME} | {d.kWh} kWh | {d.STATUS} | {d.ADDRESS}
          </li>
        ))}
      </ul>
    </div>
  );
}
