import React, { useState, useEffect } from "react";
import axios from "axios";

// DeviceForm component
function DeviceForm({ userId, onDeviceAdded }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("ON");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!deviceName || !address) return alert("Thiếu thông tin device");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: deviceName,
        ADDRESS: address,
        STATUS: status,
        USER_ID: Number(userId), // convert sang number
      });
      console.log("Device added:", res.data);

      setDeviceName("");
      setAddress("");
      setStatus("ON");

      if (onDeviceAdded) onDeviceAdded();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Lỗi khi thêm device");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: 10 }}>
      <input
        type="text"
        placeholder="Device Name"
        value={deviceName}
        onChange={(e) => setDeviceName(e.target.value)}
        style={{ marginRight: 5 }}
      />
      <input
        type="text"
        placeholder="Address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        style={{ marginRight: 5 }}
      />
      <select
        value={status}
        onChange={(e) => setStatus(e.target.value)}
        style={{ marginRight: 5 }}
      >
        {" "}
        <option value="ON">ON</option> <option value="OFF">OFF</option>{" "}
      </select>{" "}
      <button type="submit">Add Device</button>{" "}
    </form>
  );
}

// Dashboard component
function Dashboard({ userId }) {
  const [devices, setDevices] = useState([]);

  const loadDevices = async () => {
    if (!userId) return;
    try {
      const res = await axios.get(
        `http://localhost:5000/api/devices/user/${userId}`
      );
      setDevices(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadDevices();
  }, [userId]);

  return (
    <div style={{ marginTop: 20 }}>
      {" "}
      <h2>Devices for User {userId}</h2>{" "}
      <ul>
        {devices.map((d) => (
          <li key={d.DEVICE_ID}>
            {d.DEVICE_NAME} | {d.kWh} kWh | {d.STATUS}{" "}
          </li>
        ))}{" "}
      </ul>{" "}
      <DeviceForm userId={userId} onDeviceAdded={loadDevices} />{" "}
    </div>
  );
}

// Main App component
export default function App() {
  const [userId, setUserId] = useState(1);

  return (
    <div style={{ padding: 20 }}>
      {" "}
      <h1>IEMS Dashboard</h1>
      <div>
        <label>
          Enter USER_ID:{" "}
          <input
            type="number"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            style={{ marginBottom: 10 }}
          />
        </label>
      </div>
      <Dashboard userId={userId} />
    </div>
  );
}
