import React, { useState, useEffect, useRef } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import axios from "axios";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const refreshOptions = [
  { label: "2s", value: 2000 },
  { label: "5s", value: 5000 },
  { label: "10s", value: 10000 },
  { label: "30s", value: 30000 },
  { label: "1p", value: 60000 },
  { label: "5p", value: 300000 },
  { label: "10p", value: 600000 },
  { label: "30p", value: 1800000 },
  { label: "1h", value: 3600000 },
  { label: "3h", value: 10800000 },
  { label: "10h", value: 36000000 },
  { label: "24h", value: 86400000 },
];

function Dashboard({ user }) {
  const [devices, setDevices] = useState([]);
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [refreshInterval, setRefreshInterval] = useState(5000);
  const intervalRef = useRef(null);

  const fetchDevices = async () => {
    if (!user?.USER_ID) return;
    try {
      const res = await axios.get(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      setDevices(res.data);
    } catch (err) {
      console.error("❌ Error fetching devices:", err);
    }
  };

  // Filter devices based on date
  const filteredDevices = devices.filter((d) => {
    if (!fromDate && !toDate) return true;
    const deviceDate = new Date(d.lastUpdate || d.CREATE_AT || new Date());
    const from = fromDate ? new Date(fromDate) : null;
    const to = toDate ? new Date(toDate) : null;
    if (from && deviceDate < from) return false;
    if (to && deviceDate > to) return false;
    return true;
  });

  // Auto-refresh
  useEffect(() => {
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(fetchDevices, refreshInterval);
    return () => clearInterval(intervalRef.current);
  }, [refreshInterval, user]);

  const totalKwh = filteredDevices.reduce(
    (sum, d) => sum + Number(d.kWh || 0),
    0
  );

  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map(
          (d) =>
            d.color || "#" + Math.floor(Math.random() * 16777215).toString(16)
        ),
        borderColor: filteredDevices.map(
          (d) =>
            d.color || "#" + Math.floor(Math.random() * 16777215).toString(16)
        ),
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const d = filteredDevices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${
              d.STATUS || ""
            }`;
          },
        },
      },
      legend: { display: false },
    },
    scales: { y: { beginAtZero: true } },
  };

  return (
    <div
      style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}
    >
      {/* Date & refresh controls */}
      <div style={{ display: "flex", gap: 16, alignItems: "center" }}>
        <div>
          <label>From: </label>
          <input
            type="date"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
          />
        </div>
        <div>
          <label>To: </label>
          <input
            type="date"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
          />
        </div>
        <div>
          <label>Refresh every: </label>
          <select
            value={refreshInterval}
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
          >
            {refreshOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
        </div>
        <button onClick={fetchDevices}>Refresh ngay</button>
      </div>

      {/* Total kWh */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 16,
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          minWidth: 200,
        }}
      >
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>

      {/* Devices list */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {filteredDevices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              flex: "1 1 220px",
              backgroundColor: "#fff",
              borderRadius: 12,
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              padding: 16,
              borderLeft: `6px solid ${d.color || "#4caf50"}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>
              <strong>Địa chỉ:</strong> {d.ADDRESS}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                style={{ color: d.STATUS === "ON" ? "#4caf50" : "#f44336" }}
              >
                {d.STATUS}
              </span>
            </p>
            <p>
              <strong>kWh:</strong> {d.kWh}
            </p>
          </div>
        ))}
      </div>

      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ đường</h4>
          <Line data={chartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 300px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <Pie
            data={chartData}
            options={{ ...chartOptions, maintainAspectRatio: true }}
          />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
