// cspell: disable

import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function Dashboard({ user, devices = [] }) {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [selectedDevice, setSelectedDevice] = useState("all");
  const [chartData, setChartData] = useState(null);

  const fetchSummary = async () => {
    if (!fromDate || !toDate) return;

    const params = {
      USER_ID: user.USER_ID,
      fromDate,
      toDate,
      DEVICE_ID: selectedDevice === "all" ? null : selectedDevice,
    };

    try {
      const res = await axios.get(
        "http://localhost:5000/api/powerlog/summary",
        { params }
      );

      const labels = res.data.map((d) => d.DATE);
      const dataPoints = res.data.map((d) => d.totalKWh);

      setChartData({
        labels,
        datasets: [
          {
            label: "kWh tiêu thụ",
            data: dataPoints,
            borderColor: "#4caf50",
            backgroundColor: "rgba(76,175,80,0.2)",
            tension: 0.3,
          },
        ],
      });
    } catch (err) {
      console.error(err);
    }
  };

  // Tổng kWh tất cả thiết bị
  const totalKwh = devices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);

  return (
    <div style={{ padding: 16 }}>
      <h2>Dashboard Năng Lượng</h2>

      {/* Filter */}
      <div
        style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 16 }}
      >
        <div>
          <label>Từ ngày:</label>
          <input
            type="date"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
          />
        </div>
        <div>
          <label>Đến ngày:</label>
          <input
            type="date"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
          />
        </div>
        <div>
          <label>Thiết bị:</label>
          <select
            value={selectedDevice}
            onChange={(e) => setSelectedDevice(e.target.value)}
          >
            <option value="all">Tất cả</option>
            {devices.map((d) => (
              <option key={d.DEVICE_ID} value={d.DEVICE_ID}>
                {d.DEVICE_NAME}
              </option>
            ))}
          </select>
        </div>
        <button onClick={fetchSummary}>Xem</button>
      </div>

      {/* Tổng kWh */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 16,
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          marginBottom: 24,
          maxWidth: 300,
        }}
      >
        <h4>Tổng năng lượng tiêu thụ</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>

      {/* Biểu đồ */}
      <div style={{ display: "flex", gap: 24, flexWrap: "wrap" }}>
        <div
          style={{
            flex: "1 1 400px",
            minHeight: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ Line</h4>
          {chartData ? (
            <Line data={chartData} />
          ) : (
            <p>Chọn ngày để xem biểu đồ</p>
          )}
        </div>

        <div
          style={{
            flex: "1 1 400px",
            minHeight: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ Bar</h4>
          {chartData ? (
            <Bar data={chartData} />
          ) : (
            <p>Chọn ngày để xem biểu đồ</p>
          )}
        </div>
      </div>

      {/* Danh sách thiết bị */}
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: 16,
          marginTop: 24,
        }}
      >
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              flex: "1 1 220px",
              backgroundColor: "#fff",
              borderRadius: 12,
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              padding: 16,
              borderLeft: `6px solid ${d.color || "#4caf50"}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>
              <strong>Địa chỉ:</strong> {d.ADDRESS}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                style={{ color: d.STATUS === "ON" ? "#4caf50" : "#f44336" }}
              >
                {d.STATUS}
              </span>
            </p>
            <p>
              <strong>kWh:</strong> {d.kWh}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
