  const res = await axios.post("http://localhost:5000/api/devices", {
    DEVICE_NAME: deviceName,
    ADDRESS: address,
    STATUS: status,
    USER_ID: Number(userId), // convert sang number
  });
  console.log("Device added:", res.data);

  setDeviceName("");
  setAddress("");
  setStatus("ON");

  if (onDeviceAdded) onDeviceAdded();
} catch (err) {
  console.error(err);
