import React, { useState } from "react";
import axios from "axios";

export default function DeviceManager({ user, devices, fetchDevices }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("ON");
  const [kWh, setKwh] = useState(0);

  const handleAddDevice = async (e) => {
    e.preventDefault();
    if (!deviceName || !address) return alert("Thiếu thông tin device");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: deviceName,
        ADDRESS: address,
        STATUS: status,
        USER_ID: Number(user.USER_ID),
        kWh: Number(kWh), // ✅ gửi kWh lên backend
      });

      console.log("Added device:", res.data);

      if (fetchDevices) fetchDevices(); // reload devices từ backend

      setDeviceName("");
      setAddress("");
      setStatus("ON");
      setKwh(0);
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Lỗi khi thêm device");
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Device Manager</h2>
      <form onSubmit={handleAddDevice} style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Device Name"
          value={deviceName}
          onChange={(e) => setDeviceName(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <input
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{ marginRight: 8 }}
        >
          <option value="ON">ON</option>
          <option value="OFF">OFF</option>
        </select>
        <input
          type="number"
          placeholder="kWh"
          value={kWh}
          onChange={(e) => setKwh(e.target.value)}
          style={{ marginRight: 8, width: 80 }}
        />
        <button type="submit">Add Device</button>
      </form>

      <ul>
        {devices.map((d) => (
          <li key={d.DEVICE_ID}>
            {d.DEVICE_NAME} | {d.kWh} kWh | {d.STATUS}
          </li>
        ))}
      </ul>
    </div>
  );
}
