import React, { useState, useEffect } from "react";

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: "Phòng khách",
    STATUS: "On",
  });

  const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

  // Lấy dữ liệu từ server
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices-with-power/${user.USER_ID}`
        );
        if (!res.ok) throw new Error(`Server responded with ${res.status}`);
        const data = await res.json();
        setDevices(
          data.map((d) => ({
            ...d,
            color: "#" + Math.floor(Math.random() * 16777215).toString(16),
          }))
        );
      } catch (err) {
        console.error("Lỗi fetch devices:", err);
      }
    };

    fetchDevices();
  }, [user, setDevices]);

  const handleAdd = async () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    try {
      const res = await fetch("http://localhost:5000/api/devices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...form, USER_ID: user.USER_ID }),
      });
      if (!res.ok) throw new Error(`Server error: ${res.status}`);
      const newDevice = await res.json();
      newDevice.color = "#" + Math.floor(Math.random() * 16777215).toString(16);
      setDevices([...devices, newDevice]);
      setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
    } catch (err) {
      console.error("Add device error:", err);
      alert("Lưu thiết bị thất bại!");
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          maxWidth: "400px",
        }}
      >
        <h4>Thêm thiết bị</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Lưu thiết bị
        </button>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>POWER: {d.POWER || 0} W</p>
            <p>
              Time: {d.CREATE_AT ? new Date(d.CREATE_AT).toLocaleString() : "-"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
