import React from "react";

function DeviceCard({ device, onDelete, onEdit }) {
  return (
    <div
      style={{
        padding: 15,
        background: "#fff",
        borderRadius: 8,
        boxShadow: "0 0 5px rgba(0,0,0,0.1)",
        position: "relative",
        width: 250,
        margin: 10,
      }}
    >
      <h4>{device.DEVICE_NAME}</h4>
      <p>
        <strong>Device ID:</strong> {device.DEVICE_ID}
      </p>
      <p>
        <strong>User ID:</strong> {device.USER_ID}
      </p>
      <p>
        <strong>Điện tiêu thụ:</strong> {device.kWh}
      </p>
      <p>
        <strong>Ngày tạo:</strong> {device.CREATE_AT}
      </p>
      <p>
        <strong>Địa chỉ:</strong> {device.ADDRESS}
      </p>
      <p>
        <strong>Status:</strong> {device.STATUS}
      </p>

      {/* Nút xóa */}
      <button
        onClick={onDelete}
        style={{
          position: "absolute",
          top: 5,
          right: 5,
          cursor: "pointer",
        }}
      >
        ❌
      </button>

      {/* Nút sửa */}
      <button
        onClick={onEdit}
        style={{
          position: "absolute",
          top: 5,
          right: 35,
          cursor: "pointer",
        }}
      >
        ✏️
      </button>
    </div>
  );
}

export default DeviceCard;
