import React, { useState } from "react";
import Device from "./Device";

function App() {
const [selectedMenu, setSelectedMenu] = useState("Dashboard");
const [user] = useState({ USER_ID: 1, name: "Admin" });
const [devices, setDevices] = useState([]);

const renderContent = () => {
switch (selectedMenu) {
case "Dashboard":
return ( <div> <h2>Dashboard</h2> <p>Hiển thị biểu đồ và thông tin tổng hợp ở đây.</p> </div>
);
case "Device":
return (
<Device
user={user}
onDevicesUpdate={(updatedDevices) => setDevices(updatedDevices)}
/>
);
case "Settings":
return ( <div> <h2>Settings</h2> <p>Cấu hình hệ thống sẽ hiển thị ở đây.</p> </div>
);
default:
return null;
}
};

return (
<div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
{/* Sidebar */}
<div
style={{
width: "200px",
background: "#f0f0f0",
padding: "20px",
display: "flex",
flexDirection: "column",
gap: "12px",
}}
>
<button
onClick={() => setSelectedMenu("Dashboard")}
style={{ padding: "8px", cursor: "pointer" }}
>
Dashboard </button>
<button
onClick={() => setSelectedMenu("Device")}
style={{ padding: "8px", cursor: "pointer" }}
>
Device </button>
<button
onClick={() => setSelectedMenu("Settings")}
style={{ padding: "8px", cursor: "pointer" }}
>
Settings </button> </div>

  {/* Main Content */}
  <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
    {renderContent()}
  </div>
</div>

);
}

export default App;
