import React, { useState } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  // Toggle thiết bị cho Pie chart
  const [visibleDevices, setVisibleDevices] = useState(
    devices.reduce((acc, d) => ({ ...acc, [d.DEVICE_ID]: true }), {})
  );

  // Toggle chung cho Bar/Line chart
  const [showBarLine, setShowBarLine] = useState(true);

  const handleToggleDevice = (id) => {
    setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const filteredDevices = devices.filter((d) => visibleDevices[d.DEVICE_ID]);

  const totalKwh = filteredDevices.reduce(
    (sum, d) => sum + Number(d.kWh || 0),
    0
  );

  const chartLabels = filteredDevices.map((d) => d.DEVICE_NAME);

  const chartData = {
    labels: chartLabels,
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#af4c8bff"),
        borderColor: "#333",
        borderWidth: 2,
        tension: 0.4, // đường cong cho Line chart
      },
    ],
  };

  const pieData = {
    labels: chartLabels,
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            const d = filteredDevices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS} | ${d.STATUS}`;
          },
        },
      },
      legend: { position: "top" },
    },
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "24px",
        padding: "16px",
      }}
    >
      {/* Toggle Bar/Line
      <div style={{ marginBottom: "12px" }}>
        {" "}
        <label>
          <input
            type="checkbox"
            checked={showBarLine}
            onChange={() => setShowBarLine(!showBarLine)}
          />
          Hiển thị Bar/Line chart{" "}
        </label>{" "}
      </div> */}

      {/* Toggle Pie chart theo thiết bị */}
      <div style={{ marginBottom: "12px" }}>
        {devices.map((d) => (
          <label key={d.DEVICE_ID} style={{ marginRight: "12px" }}>
            <input
              type="checkbox"
              checked={visibleDevices[d.DEVICE_ID]}
              onChange={() => handleToggleDevice(d.DEVICE_ID)}
            />
            {d.DEVICE_NAME}
          </label>
        ))}
      </div>

      {/* Biểu đồ */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "24px" }}>
        {showBarLine && (
          <>
            {/* Bar Chart */}
            <div
              style={{
                flex: "1 1 400px",
                height: "300px",
                backgroundColor: "#fff",
                borderRadius: "12px",
                padding: "16px",
                boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              }}
            >
              <h4>Biểu đồ cột</h4>
              <Bar data={chartData} options={options} />
            </div>

            {/* Line Chart */}
            <div
              style={{
                flex: "1 1 400px",
                height: "300px",
                backgroundColor: "#fff",
                borderRadius: "12px",
                padding: "16px",
                boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              }}
            >
              <h4>Biểu đồ đường</h4>
              <Line data={chartData} options={options} />
            </div>
          </>
        )}

        {/* Pie Chart */}
        <div
          style={{
            flex: "1 1 300px",
            height: "300px",
            backgroundColor: "#fff",
            borderRadius: "12px",
            padding: "16px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <div style={{ width: "80%", height: "80%" }}>
            <Pie
              data={pieData}
              options={{ ...options, maintainAspectRatio: true }}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
