import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
i
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Load devices (mock data tạm thời)
  useEffect(() => {
    if (!user) return;

    const fetchDevices = () => {
      const mockDevices = [
        {
          DEVICE_ID: 1,
          DEVICE_NAME: "Máy lạnh",
          ADDRESS: "Phòng khách",
          STATUS: "On",
          kWh: 880,
          color: "#4caf50",
        },
        {
          DEVICE_ID: 2,
          DEVICE_NAME: "Tivi",
          ADDRESS: "Phòng ngủ",
          STATUS: "Off",
          kWh: 110,
          color: "#f44336",
        },
        {
          DEVICE_ID: 3,
          DEVICE_NAME: "Quạt",
          ADDRESS: "Nhà bếp",
          STATUS: "On",
          kWh: 176,
          color: "#2196f3",
        },
      ];
      setDevices(mockDevices);
    };

    fetchDevices();
  }, [user]);

  // Logout
  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  // Thêm device mới
  const addDevice = (device) => {
    const newDevice = {
      ...device,
      DEVICE_ID: devices.length + 1,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      kWh: 0, // Giá trị mặc định
    };
    setDevices((prev) => [...prev, newDevice]);
  };

  // Render content theo menu
  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device user={user} devices={devices} addDevice={addDevice} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container" style={{ display: "flex", height: "100vh" }}>
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div
        className="main-content"
        style={{ flex: 1, padding: "16px", overflowY: "auto" }}
      >
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
