import React from "react";

function Dashboard({ devices = [] }) {
  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
      {devices.map(d => (
        <div key={d.DEVICE_ID} style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px", minWidth: "200px", borderLeft: `4px solid ${d.color}` }}>
          <h4>{d.DEVICE_NAME}</h4>
          <p>Địa chỉ: {d.ADDRESS}</p>
          <p>Status: {d.STATUS}</p>
        </div>
      ))}
      <div style={{ flex: "1 1 100%", padding: "16px" }}>
        <p><strong>Số thiết bị:</strong> {devices.length}</p>
      </div>
    </div>
  );
}

export default Dashboard;
