/* Dashboard.js */
import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  const totalKwh = devices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);

  const chartLabels = devices.map((d) => d.name);
  const chartData = {
    labels: chartLabels,
    datasets: [
      {
        label: "kWh",
        data: devices.map((d) => Number(d.kWh || 0)),
        borderWidth: 2,
        backgroundColor: devices.map((d) => d.color || "#888888"),
        borderColor: devices.map((d) => d.color || "#888888"),
      },
    ],
  };

  const pieData = {
    labels: chartLabels,
    datasets: [
      {
        data: devices.map((d) => Number(d.kWh || 0)),
        backgroundColor: devices.map((d) => d.color || "#888888"),
        borderColor: devices.map((d) => d.color || "#888888"),
        borderWidth: 1,
      },
    ],
  };

  const options = {
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            const d = devices[context.dataIndex];
            return `${d.name}: ${d.kWh} kWh | ${d.device_id} | ${d.user_id} | ${d.address}`;
          },
        },
      },
    },
  };

  return (
    <div className="dashboard-charts">
      <div className="chart-box" style={{ height: 250 }}>
        {" "}
        <h3>Biểu đồ cột</h3> <Bar data={chartData} options={options} />{" "}
      </div>
      <div className="chart-box" style={{ height: 250 }}>
        {" "}
        <h3>Biểu đồ đường</h3> <Line data={chartData} options={options} />{" "}
      </div>
      <div className="chart-box" style={{ height: 250 }}>
        {" "}
        <h3>Biểu đồ</h3> <Pie data={pieData} options={options} />{" "}
      </div>{" "}
      <div className="chart-box">
        {" "}
        <p>Tổng năng lượng: {totalKwh} kWh</p>{" "}
        <p>Số thiết bị: {devices.length}</p>{" "}
      </div>{" "}
    </div>
  );
}

export default Dashboard;
