import React, { useState, useEffect } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
Chart as ChartJS,
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend,
} from "chart.js";

ChartJS.register(
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend
);

function Dashboard({ devices = [], fetchData }) {
const [visibleDevices, setVisibleDevices] = useState(
devices.reduce((acc, d) => ({ ...acc, [d.DEVICE_ID]: true }), {})
);
const [timeRange, setTimeRange] = useState("day"); // day | week | month
const [filteredDevices, setFilteredDevices] = useState(devices);

const handleToggleDevice = (id) => {
setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
};

const handleTimeChange = (e) => {
setTimeRange(e.target.value);
};

// Filter devices by toggle and time
const filterDevices = (allDevices) => {
const now = new Date();
return allDevices.filter((d) => {
if (!visibleDevices[d.DEVICE_ID]) return false;
const deviceDate = new Date(d.timestamp || now);
if (timeRange === "day") return deviceDate.toDateString() === now.toDateString();
if (timeRange === "week") {
const weekStart = new Date(now);
weekStart.setDate(now.getDate() - now.getDay());
return deviceDate >= weekStart && deviceDate <= now;
}
if (timeRange === "month") return deviceDate.getMonth() === now.getMonth() && deviceDate.getFullYear() === now.getFullYear();
return true;
});
};

// Auto-update data
useEffect(() => {
const interval = setInterval(async () => {
if (fetchData) {
const newDevices = await fetchData();
setFilteredDevices(filterDevices(newDevices));
}
}, 10000); // 10s update
return () => clearInterval(interval);
}, [visibleDevices, timeRange, fetchData]);

// Initial filter & when toggle/time changes
useEffect(() => {
  const now = new Date();
  const filtered = devices.filter((d) => {
    if (!visibleDevices[d.DEVICE_ID]) return false;
    const deviceDate = new Date(d.timestamp || now);
    if (timeRange === "day") return deviceDate.toDateString() === now.toDateString();
    if (timeRange === "week") {
      const weekStart = new Date(now);
      weekStart.setDate(now.getDate() - now.getDay());
      return deviceDate >= weekStart && deviceDate <= now;
    }
    if (timeRange === "month") return deviceDate.getMonth() === now.getMonth() && deviceDate.getFullYear() === now.getFullYear();
    return true;
  });
  setFilteredDevices(filtered);
}, [devices, visibleDevices, timeRange]);


const totalKwh = filteredDevices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);
const chartLabels = filteredDevices.map((d) => d.DEVICE_NAME);

const chartData = {
labels: chartLabels,
datasets: [
{
label: "kWh",
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderColor: "#333",
borderWidth: 2,
tension: 0.4,
},
],
};

const pieData = {
labels: chartLabels,
datasets: [
{
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderColor: "#fff",
borderWidth: 4,
},
],
};

const options = {
responsive: true,
maintainAspectRatio: false,
plugins: {
tooltip: {
callbacks: {
label: (context) => {
const d = filteredDevices[context.dataIndex];
return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${d.STATUS || ""}`;
},
},
},
legend: { position: "top" },
},
};

return (
<div style={{ display: "flex", flexDirection: "column", gap: "24px", padding: "16px" }}>
{/* Time filter */}
<div style={{ marginBottom: "12px" }}>
<label style={{ marginRight: "12px" }}>
Time Range:
<select value={timeRange} onChange={handleTimeChange} style={{ marginLeft: "8px" }}> <option value="day">Hôm nay</option> <option value="week">Tuần này</option> <option value="month">Tháng này</option> </select> </label> </div>

```
  {/* Device toggle */}
  <div style={{ marginBottom: "12px" }}>
    {devices.map((d) => (
      <label key={d.DEVICE_ID} style={{ marginRight: "12px" }}>
        <input
          type="checkbox"
          checked={visibleDevices[d.DEVICE_ID]}
          onChange={() => handleToggleDevice(d.DEVICE_ID)}
        />
        {d.DEVICE_NAME}
      </label>
    ))}
  </div>

  {/* Total & device cards */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
    <div style={{ backgroundColor: "#fff", padding: "16px", borderRadius: "12px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", minWidth: "200px" }}>
      <h4>Tổng năng lượng</h4>
      <p style={{ fontSize: "24px", fontWeight: "bold", margin: 0 }}>{totalKwh} kWh</p>
    </div>
    {filteredDevices.map((d) => (
      <div key={d.DEVICE_ID} style={{ backgroundColor: "#fff", padding: "12px", borderRadius: "12px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", minWidth: "180px" }}>
        <h5>{d.DEVICE_NAME}</h5>
        <p style={{ margin: "4px 0" }}>kWh: {d.kWh}</p>
        <p style={{ margin: "4px 0" }}>Trạng thái: {d.STATUS || "N/A"}</p>
        <p style={{ margin: "4px 0" }}>Địa chỉ: {d.ADDRESS || "N/A"}</p>
      </div>
    ))}
  </div>

  {/* Charts */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "24px", marginTop: "16px" }}>
    <div style={{ flex: "1 1 400px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
      <h4>Biểu đồ cột</h4>
      <Bar data={chartData} options={options} />
    </div>
    <div style={{ flex: "1 1 400px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
      <h4>Biểu đồ đường</h4>
      <Line data={chartData} options={options} />
    </div>
    <div style={{ flex: "1 1 300px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center" }}>
      <h4>Biểu đồ tròn</h4>
      <div style={{ width: "80%", height: "80%" }}>
        <Pie data={pieData} options={{ ...options, maintainAspectRatio: true }} />
      </div>
    </div>
  </div>
</div>


);
}

export default Dashboard;
