import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
Chart as ChartJS, CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend);

function Dashboard({ devices }) {
const totalKwh = devices.reduce((sum,d)=>sum+Number(d.kWh||0),0);
const labels = devices.map(d=>d.DEVICE_NAME);

const chartData = {
labels,
datasets: [{
label:"kWh",
data: devices.map(d=>d.kWh),
backgroundColor: devices.map(d=>d.color)
}]
};

const options = { responsive:true, plugins:{ legend:{ position:"top" } } };

return ( <div> <h3>Tổng năng lượng: {totalKwh} kWh</h3>
<div style={{ display:"flex", gap:"16px", flexWrap:"wrap" }}>
<div style={{ flex:1, minWidth:300, height:300 }}><Bar data={chartData} options={options} /></div>
<div style={{ flex:1, minWidth:300, height:300 }}><Line data={chartData} options={options} /></div>
<div style={{ flex:1, minWidth:300, height:300 }}><Pie data={chartData} options={options} /></div> </div> </div>
);
}

export default Dashboard;
