import React, { useState, useEffect } from "react";
import Device from "./Device";
import Dashboard from "";
import axios from "axios";

function App() {
  const [devices, setDevices] = useState([]);
  const [user, setUser] = useState({ USER_ID: 1 }); // giả lập user

  // Lấy devices từ server khi App load
  const fetchDevices = async () => {
    try {
      const res = await axios.get(
        "[http://localhost:5000/api/devices](http://localhost:5000/api/devices)"
      );
      setDevices(
        res.data.map((d) => ({
          ...d,
          color: "#" + Math.floor(Math.random() * 16777215).toString(16),
        }))
      );
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, []);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "20px",
        padding: "20px",
      }}
    >
      {" "}
      <h2>Device Dashboard</h2>{" "}
      <Device user={user} devices={devices} setDevices={setDevices} />{" "}
      <Dashboard devices={devices} />{" "}
    </div>
  );
}

export default App;
