import React, { useState } from "react";
import styles from "../styles/DeviceCard.module.css";

function DeviceCard({ device, onRemove }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={styles.card}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <h3>{device.name}</h3>
      <p>Consumption: {device.kWh} kWh</p>
      <p>Usage Time: {device.hours} hours</p>
      {isHovered && <button onClick={() => onRemove(device.id)}>XÃ³a</button>}
    </div>
  );
}

export default DeviceCard;
