/* Device.js */
import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    time: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load devices từ server khi component mount
  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices");
      const devicesWithColor = res.data.map((d) => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  // Thêm device vào local state (hiển thị ngay trên Device card)
  const handleAddLocal = () => {
    if (!form.DEVICE_NAME || !form.kWh || !form.time) return;

    const newDevice = {
      ...form,
      DEVICE_ID: devices.length + 1, // temporary ID
      USER_ID: 300000,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    };
    setDevices([...devices, newDevice]);

    setForm({
      DEVICE_NAME: "",
      kWh: "",
      time: "",
      ADDRESS: addresses[0],
      STATUS: "On",
    });
  };

  // Lưu device vào SQL Server
  const handleSaveSQL = async (device) => {
    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: device.DEVICE_NAME,
        kWh: device.kWh,
        time: device.time,
        ADDRESS: device.ADDRESS,
        STATUS: device.STATUS,
        USER_ID: device.USER_ID,
      });
      console.log("Saved to SQL:", res.data);
      // Nếu cần, fetch lại devices từ server để đồng bộ
      fetchDevices();
    } catch (err) {
      console.error("Error saving device:", err);
    }
  };

  return (
    <div
      className="device-grid"
      style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}
    >
      {/* Form thêm thiết bị */}
      <div
        className="device-card"
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
        }}
      >
        <h4>Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
        />
        <input
          type="number"
          placeholder="kWh"
          value={form.kWh}
          onChange={(e) => setForm({ ...form, kWh: e.target.value })}
        />
        <input
          type="text"
          placeholder="Thời gian"
          value={form.time}
          onChange={(e) => setForm({ ...form, time: e.target.value })}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button
          onClick={handleAddLocal}
          style={{ marginTop: "8px", marginRight: "8px" }}
        >
          Thêm Device
        </button>
      </div>

      {/* Hiển thị devices */}
      {devices.map((d) => (
        <div
          key={d.DEVICE_ID}
          className="device-card"
          style={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "8px",
            borderLeft: `4px solid ${d.color || "#000"}`,
          }}
        >
          <h4>{d.DEVICE_NAME}</h4>
          <p>kWh: {d.kWh}</p>
          <p>Thời gian: {d.time}</p>
          <p>Địa chỉ: {d.ADDRESS}</p>
          <p>Status: {d.STATUS}</p>
          <button onClick={() => handleSaveSQL(d)} style={{ marginTop: "8px" }}>
            Lưu vào SQL
          </button>
        </div>
      ))}
    </div>
  );
}

export default Device;
