import React, { useState } from "react";
import DeviceCard from "./DeviceCard";

function Device() {
  const [devices, setDevices] = useState([
    {
      id: 1,
      device_id: "D001",
      user_id: "U001",
      name: "Máy lạnh",
      kWh: 12,
      time: "10:00 - 12:00",
      address: "Phòng khách",
      status: "On",
    },
    {
      id: 2,
      device_id: "D002",
      user_id: "U002",
      name: "Tivi",
      kWh: 5,
      time: "18:00 - 20:00",
      address: "Phòng ngủ",
      status: "Off",
    },
  ]);

  // State thêm thiết bị
  const [newDeviceId, setNewDeviceId] = useState("");
  const [newUserId, setNewUserId] = useState("");
  const [newName, setNewName] = useState("");
  const [newKwh, setNewKwh] = useState("");
  const [newTime, setNewTime] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newStatus, setNewStatus] = useState("");

  // State edit
  const [editing, setEditing] = useState(null);
  const [editDeviceId, setEditDeviceId] = useState("");
  const [editUserId, setEditUserId] = useState("");
  const [editName, setEditName] = useState("");
  const [editKwh, setEditKwh] = useState("");
  const [editTime, setEditTime] = useState("");
  const [editAddress, setEditAddress] = useState("");
  const [editStatus, setEditStatus] = useState("");

  // Thêm thiết bị
  const addDevice = () => {
    if (
      !newDeviceId ||
      !newUserId ||
      !newName ||
      !newKwh ||
      !newTime ||
      !newAddress ||
      !newStatus
    ) {
      alert("Nhập đầy đủ thông tin!");
      return;
    }


const newDevice = {  
  id: Date.now(),  
  device_id: newDeviceId,  
  user_id: newUserId,  
  name: newName,  
  kWh: Number(newKwh),  
  time: newTime,  
  address: newAddress,  
  status: newStatus  
};  

setDevices([...devices, newDevice]);  

// Reset input  
setNewDeviceId("");  
setNewUserId("");  
setNewName("");  
setNewKwh("");  
setNewTime("");  
setNewAddress("");  
setNewStatus("");  
;
  };

  // Mở modal sửa thiết bị
  const openEdit = (device) => {
    setEditing(device.id);
    setEditDeviceId(device.device_id);
    setEditUserId(device.user_id);
    setEditName(device.name);
    setEditKwh(device.kWh);
    setEditTime(device.time);
    setEditAddress(device.address);
    setEditStatus(device.status);
  };

  // Lưu sửa
  const saveEdit = () => {
    const updated = devices.map((d) =>
      d.id === editing
        ? {
            ...d,
            device_id: editDeviceId,
            user_id: editUserId,
            name: editName,
            kWh: Number(editKwh),
            time: editTime,
            address: editAddress,
            status: editStatus,
          }
        : d
    );
    setDevices(updated);
    setEditing(null);
  };

  // Xóa thiết bị
  const deleteDevice = (id) => {
    setDevices(devices.filter((d) => d.id !== id));
  };

  return (
    <div>
      <h2 style={{ marginLeft: 20 }}>Device</h2>
      ```
      {/* Form thêm thiết bị */}
      <div style={{ margin: 20 }}>
        <input
          type="text"
          placeholder="Device ID..."
          value={newDeviceId}
          onChange={(e) => setNewDeviceId(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />
        <input
          type="text"
          placeholder="User ID..."
          value={newUserId}
          onChange={(e) => setNewUserId(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />
        <input
          type="text"
          placeholder="Tên thiết bị..."
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />
        <input
          type="number"
          placeholder="kWh"
          value={newKwh}
          onChange={(e) => setNewKwh(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 80 }}
        />
        <input
          type="text"
          placeholder="Thời gian (VD: 10:00 - 12:00)"
          value={newTime}
          onChange={(e) => setNewTime(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 180 }}
        />
        <input
          type="text"
          placeholder="Địa chỉ"
          value={newAddress}
          onChange={(e) => setNewAddress(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 150 }}
        />
        <input
          type="text"
          placeholder="Status"
          value={newStatus}
          onChange={(e) => setNewStatus(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 80 }}
        />
        <button className="add-device-btn" onClick={addDevice}>
          + Add Device
        </button>
      </div>
      {/* Danh sách thiết bị */}
      <div className="device-grid">
        {devices.map((d) => (
          <DeviceCard
            key={d.id}
            device={d}
            onDelete={() => deleteDevice(d.id)}
            onEdit={() => openEdit(d)}
          />
        ))}
      </div>
      {/* MODAL EDIT */}
      {editing && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              width: 350,
              borderRadius: 10,
            }}
          >
            <h3>Sửa thiết bị</h3>
            <input
              type="text"
              value={editDeviceId}
              onChange={(e) => setEditDeviceId(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="Device ID"
            />
            <input
              type="text"
              value={editUserId}
              onChange={(e) => setEditUserId(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="User ID"
            />
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="Tên thiết bị"
            />
            <input
              type="number"
              value={editKwh}
              onChange={(e) => setEditKwh(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="kWh"
            />
            <input
              type="text"
              value={editTime}
              onChange={(e) => setEditTime(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="Thời gian"
            />
            <input
              type="text"
              value={editAddress}
              onChange={(e) => setEditAddress(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="Địa chỉ"
            />
            <input
              type="text"
              value={editStatus}
              onChange={(e) => setEditStatus(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
              placeholder="Status"
            />

            <button onClick={saveEdit} style={{ marginRight: 10 }}>
              Lưu
            </button>
            <button onClick={() => setEditing(null)}>Hủy</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Device;
