import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    date: new Date().toISOString().slice(0, 10),
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load devices từ server
  const fetchDevices = async () => {
    try {
      const res = await axios.get(
        "[http://localhost:5000/api/devices](http://localhost:5000/api/devices)"
      );
      const devicesWithColor = res.data.map((d) => ({
        ...d,
        kWh: 0,
        date: new Date().toISOString().slice(0, 10),
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

