import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Load devices sau khi login
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        // Sau này đây sẽ gọi API kết hợp DEVICE + POWER_LOG
        const res = await fetch(
          `http://localhost:5000/api/devices-with-power/${user.USER_ID}`
        );
        if (!res.ok) {
          console.warn(`Server responded with status: ${res.status}`);
          setDevices([]);
          return;
        }
        const data = await res.json();
        setDevices(Array.isArray(data) ? data : []);
        console.log("DEVICE DATA:", data);
      } catch (err) {
        console.error("Device load error:", err);
        setDevices([]);
      }
    };

    fetchDevices();
  }, [user]);

  // Logout
  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  // Add device mới
  const addDevice = async (device) => {
    try {
      const res = await fetch(
        "[http://localhost:5000/api/devices](http://localhost:5000/api/devices)",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(device),
        }
      );

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Server error: ${res.status} - ${text}`);
      }

      const newDevice = await res.json();
      setDevices((prev) => [...prev, newDevice]);
      console.log("Device added:", newDevice);
    } catch (err) {
      console.error("Add device error:", err);
    }
  };

  // Render content theo menu
  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
      break;
    case "Device":
      content = (
        <Device
          user={user}
          devices={Array.isArray(devices) ? devices : []}
          setDevices={setDevices}
          addDevice={addDevice}
        />
      );
      break;
    case "Setting":
      content = <Setting user={user} />;
      break;
    default:
      content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
  }

  // Nếu chưa login thì hiện login
  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container" style={{ display: "flex", height: "100vh" }}>
      {" "}
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div
        className="main-content"
        style={{ flex: 1, padding: "16px", overflowY: "auto" }}
      >
        {" "}
        <Header user={user} onLogout={handleLogout} />
        {content}{" "}
      </div>{" "}
    </div>
  );
}

export default App;
