  {/* Form thêm thiết bị */}  
  <div style={{ margin: 20 }}>  
    <input type="text" placeholder="Device ID..." value={newDeviceId} onChange={(e) => setNewDeviceId(e.target.value)} style={{ padding: 8, marginRight: 10 }} />  
    <input type="text" placeholder="User ID..." value={newUserId} onChange={(e) => setNewUserId(e.target.value)} style={{ padding: 8, marginRight: 10 }} />  
    <input type="text" placeholder="Tên thiết bị..." value={newName} onChange={(e) => setNewName(e.target.value)} style={{ padding: 8, marginRight: 10 }} />  
    <input type="number" placeholder="kWh" value={newKwh} onChange={(e) => setNewKwh(e.target.value)} style={{ padding: 8, marginRight: 10, width: 80 }} />  
    <input type="text" placeholder="Thời gian (VD: 10:00 - 12:00)" value={newTime} onChange={(e) => setNewTime(e.target.value)} style={{ padding: 8, marginRight: 10, width: 180 }} />  
    <input type="text" placeholder="Địa chỉ" value={newAddress} onChange={(e) => setNewAddress(e.target.value)} style={{ padding: 8, marginRight: 10, width: 150 }} />  
    <input type="text" placeholder="Status" value={newStatus} onChange={(e) => setNewStatus(e.target.value)} style={{ padding: 8, marginRight: 10, width: 80 }} />  
    <button className="add-device-btn" onClick={addDevice}>+ Add Device</button>  
  </div>  

  {/* Danh sách thiết bị */}  
  <div className="device-grid">  
    {devices.map((d) => (  
      <DeviceCard key={d.id} device={d} onDelete={() => deleteDevice(d.id)} onEdit={() => openEdit(d)} />  
    ))}  
  </div>  

  {/* MODAL EDIT */}  
  {editing && (  
    <div style={{ position: "fixed", top: 0, left: 0, width: "100%", height: "100%", background: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center" }}>  
      <div style={{ background: "#fff", padding: 20, width: 350, borderRadius: 10 }}>  
        <h3>Sửa thiết bị</h3>  
        <input type="text" value={editDeviceId} onChange={(e) => setEditDeviceId(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="Device ID" />  
        <input type="text" value={editUserId} onChange={(e) => setEditUserId(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="User ID" />  
        <input type="text" value={editName} onChange={(e) => setEditName(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="Tên thiết bị" />  
        <input type="number" value={editKwh} onChange={(e) => setEditKwh(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="kWh" />  
        <input type="text" value={editTime} onChange={(e) => setEditTime(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="Thời gian" />  
        <input type="text" value={editAddress} onChange={(e) => setEditAddress(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="Địa chỉ" />  
        <input type="text" value={editStatus} onChange={(e) => setEditStatus(e.target.value)} style={{ padding: 8, width: "100%", marginBottom: 10 }} placeholder="Status" />  

        <button onClick={saveEdit} style={{ marginRight: 10 }}>Lưu</button>  
        <button onClick={() => setEditing(null)}>Hủy</button>  
      </div>  
    </div>  
  )}  
</div>  

);
}

export default Device;
