import React, { useState, useEffect } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function DeviceManager({ user, onDevicesUpdate }) {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load devices từ server khi mount hoặc user thay đổi
  useEffect(() => {
    if (!user?.USER_ID) return; // chặn fetch khi chưa login

    const fetchDevices = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices/user/${user.USER_ID}`
        );
        const data = await res.json();
        const colored = data.map((d) => ({
          ...d,
          color: "#" + Math.floor(Math.random() * 16777215).toString(16),
        }));
        setDevices(colored);
        if (onDevicesUpdate) onDevicesUpdate(colored);
      } catch (err) {
        console.error(err);
      }
    };
    fetchDevices();
  }, [user, onDevicesUpdate]);

  // Thêm thiết bị mới
  const handleAdd = async () => {
    if (!form.DEVICE_NAME.trim()) return alert("Nhập tên thiết bị");

    const payload = { ...form, USER_ID: user.USER_ID }; // đảm bảo USER_ID luôn là number

    try {
      const res = await fetch("http://localhost:5000/api/devices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const newDevice = await res.json();
      newDevice.color = "#" + Math.floor(Math.random() * 16777215).toString(16);

      const updatedDevices = [...devices, newDevice];
      setDevices(updatedDevices);
      if (onDevicesUpdate) onDevicesUpdate(updatedDevices);

      setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
    } catch (err) {
      console.error(err);
      alert("Thêm thiết bị thất bại");
    }
  };

  return (
    <div
      style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}
    >
      {/* Form thêm thiết bị */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 26,
          borderRadius: 26,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
        }}
      >
        <h3>Thêm thiết bị mới</h3>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{
            width: "100%",
            marginBottom: 12,
            padding: 8,
            borderRadius: 6,
            border: "1px solid #ccc",
          }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{
            width: "100%",
            marginBottom: 12,
            padding: 8,
            borderRadius: 6,
            border: "1px solid #ccc",
          }}
        >
          {addresses.map((a) => (
            <option key={a}>{a}</option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{
            width: "100%",
            marginBottom: 12,
            padding: 8,
            borderRadius: 6,
            border: "1px solid #ccc",
          }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button
          onClick={handleAdd}
          style={{
            width: "100%",
            padding: 10,
            borderRadius: 6,
            border: "none",
            backgroundColor: "#4caf50",
            color: "#fff",
            fontWeight: "bold",
            cursor: "pointer",
          }}
        >
          Thêm thiết bị
        </button>
      </div>

      {/* Danh sách thiết bị */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              flex: "1 1 220px",
              backgroundColor: "#fff",
              borderRadius: 12,
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              padding: 16,
              borderLeft: `6px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>
              <strong>Địa chỉ:</strong> {d.ADDRESS}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                style={{ color: d.STATUS === "On" ? "#4caf50" : "#f44336" }}
              >
                {d.STATUS}
              </span>
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DeviceManager;
