import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import DeviceManager from "./components/DeviceManager";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null); // null = chưa login
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  const handleLogout = () => {
    setUser(null);
    setDevices([]);
  };

  // Load devices khi user thay đổi
  useEffect(() => {
    if (!user?.USER_ID) return;

    const fetchDevices = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices/user/${user.USER_ID}`
        );
        const data = await res.json();
        setDevices(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchDevices();
  }, [user]);

  // Nếu chưa login → show login
  if (!user) return <Login onLogin={setUser} />;

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <DeviceManager user={user} onDevicesUpdate={setDevices} />;
      break;
    case "Setting":
      content = <Setting />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div className="app-container">
      <Sidebar
        activeMenu={activeMenu}
        setActiveMenu={setActiveMenu}
        user={user}
        onLogout={handleLogout}
      />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
