import React, { useState, useEffect } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

// Dữ liệu mock
const MOCK_DEVICES = [
  { DEVICE_ID: 1, DEVICE_NAME: "Máy lạnh", ADDRESS: "Phòng khách", STATUS: "On", kWh: 880 },
  { DEVICE_ID: 2, DEVICE_NAME: "Tivi", ADDRESS: "Phòng ngủ", STATUS: "Off", kWh: 110 },
  { DEVICE_ID: 3, DEVICE_NAME: "Quạt", ADDRESS: "Nhà bếp", STATUS: "On", kWh: 176 },
];

function Device() {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load dữ liệu mock khi component mount
  useEffect(() => {
    const devicesWithColor = MOCK_DEVICES.map((d) => ({
      ...d,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    }));
    setDevices(devicesWithColor);
  }, []);

  const handleAdd = () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    const newDevice = {
      DEVICE_ID: devices.length + 1,
      ...form,
      kWh: 0,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    };
    setDevices([...devices, newDevice]);
    setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      <div style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px", maxWidth: "400px" }}>
        <h4>Thêm thiết bị</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>{a}</option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>Lưu thiết bị</button>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>POWER: {d.kWh} W</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
