import React, { useState, useEffect } from "react";

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: "Phòng khách",
    STATUS: "On",
  });

  const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

  // Dữ liệu cứng (mock data)
  const mockDevices = [
    {
      DEVICE_ID: 1,
      DEVICE_NAME: "Đèn phòng khách",
      ADDRESS: "Phòng khách",
      STATUS: "On",
      kWh: 12,
      CREATE_AT: new Date(),
    },
    {
      DEVICE_ID: 2,
      DEVICE_NAME: "Quạt phòng ngủ",
      ADDRESS: "Phòng ngủ",
      STATUS: "Off",
      kWh: 5,
      CREATE_AT: new Date(),
    },
    {
      DEVICE_ID: 3,
      DEVICE_NAME: "Máy lạnh phòng làm việc",
      ADDRESS: "Phòng làm việc",
      STATUS: "On",
      kWh: 20,
      CREATE_AT: new Date(),
    },
  ];

  // Khi user thay đổi, gán dữ liệu cứng
  useEffect(() => {
    if (!user) return;

    const devicesWithColor = mockDevices.map((d) => ({
      ...d,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    }));
    setDevices(devicesWithColor);
  }, [user, setDevices]);

  // Thêm thiết bị mới vào danh sách cứng
  const handleAdd = () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    const newDevice = {
      DEVICE_ID: devices.length + 1,
      ...form,
      kWh: 0,
      CREATE_AT: new Date(),
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    };

    setDevices([...devices, newDevice]);
    setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
  };

  return (
    <div style={{ marginTop: "20px" }}>
      {" "}
      <h3>Thêm Device</h3>
      <input
        type="text"
        placeholder="Tên thiết bị"
        value={form.DEVICE_NAME}
        onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
      />
      <select
        value={form.ADDRESS}
        onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
      >
        {addresses.map((a) => (
          <option key={a} value={a}>
            {a}{" "}
          </option>
        ))}{" "}
      </select>
      <select
        value={form.STATUS}
        onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
      >
        {" "}
        <option>On</option> <option>Off</option>{" "}
      </select>{" "}
      <button onClick={handleAdd}>Lưu thiết bị</button>
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          marginTop: "20px",
          gap: "16px",
        }}
      >
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "16px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>kWh: {d.kWh != null ? d.kWh : 0}</p>
            <p>
              Time: {d.CREATE_AT ? new Date(d.CREATE_AT).toLocaleString() : "-"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
