import React, { useState } from "react";

function DeviceForm({ onAdd, user }) {
  const [deviceName, setDeviceName] = useState("");
  const [address, setAddress] = useState("");
  const [status, setStatus] = useState("Off");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!deviceName || !address || !status || !user?.USER_ID) {
      return alert("Vui lòng điền đầy đủ thông tin và login");
    }

    try {
      const res = await fetch("http://localhost:5000/api/devices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          DEVICE_NAME: deviceName,
          ADDRESS: address,
          STATUS: status,
          USER_ID: user.USER_ID,
        }),
      });
      const newDevice = await res.json();
      onAdd(newDevice);

      setDeviceName("");
      setAddress("");
      setStatus("Off");
    } catch (err) {
      console.error(err);
      alert("Thêm device thất bại");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 16 }}>
      <input placeholder="Tên thiết bị" value={deviceName} onChange={(e) => setDeviceName(e.target.value)} />
      <select value={address} onChange={(e) => setAddress(e.target.value)}>
        <option value="">Chọn phòng</option>
        <option value="Phòng khách">Phòng khách</option>
        <option value="Phòng ngủ">Phòng ngủ</option>
        <option value="Nhà bếp">Nhà bếp</option>
        <option value="Phòng làm việc">Phòng làm việc</option>
      </select>
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="On">On</option>
        <option value="Off">Off</option>
      </select>
      <button type="submit">Thêm thiết bị</button>
    </form>
  );
}

export default DeviceForm;
