import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import Device from "./Device";

function App() {
  const [devices, setDevices] = useState([]);

  const [activeMenu, setActiveMenu] = useState("Dashboard");

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device devices={devices} setDevices={setDevices} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        padding: "16px",
        gap: "16px",
      }}
    >
      <div style={{ display: "flex", gap: "16px" }}>
        <button onClick={() => setActiveMenu("Dashboard")}>Dashboard</button>
        <button onClick={() => setActiveMenu("Device")}>Device</button>
      </div>
      {content}
    </div>
  );
}

export default App;
