import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, onDevicesUpdate }) {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "active",
  });

  useEffect(() => { fetchDevices(); }, []);

  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices");
      const devicesWithColor = res.data.map(d => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
      if (onDevicesUpdate) onDevicesUpdate(devicesWithColor);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.ADDRESS) return;
    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        USER_ID: user.USER_ID,
        DEVICE_NAME: form.DEVICE_NAME,
        ADDRESS: form.ADDRESS,
        STATUS: form.STATUS,
      });
      const newDevices = [...devices, { ...res.data, color: "#" + Math.floor(Math.random() * 16777215).toString(16) }];
      setDevices(newDevices);
      if (onDevicesUpdate) onDevicesUpdate(newDevices);
      setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "active" });
    } catch (err) {
      console.error(err);
      alert("Thêm thiết bị thất bại!");
    }
  };

  return (
    <div>
      <div style={{ padding: 16, border: "1px solid #ccc", borderRadius: 8, marginBottom: 20 }}>
        <h4>Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={e => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ marginRight: 8 }}
        />
        <select
          value={form.ADDRESS}
          onChange={e => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ marginRight: 8 }}
        >
          {addresses.map(a => <option key={a} value={a}>{a}</option>)}
        </select>
        <select
          value={form.STATUS}
          onChange={e => setForm({ ...form, STATUS: e.target.value })}
          style={{ marginRight: 8 }}
        >
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <button onClick={handleAdd}>Lưu vào SQL</button>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {devices.map(d => (
          <div key={d.DEVICE_ID} style={{
            padding: 16, border: "1px solid #ccc", borderRadius: 8, borderLeft: `4px solid ${d.color || "#000"}`, flex: "1 1 200px"
          }}>
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
