import React, { useState } from "react";
import DeviceCard from "./DeviceCard";

function Device() {
  const [devices, setDevices] = useState([
    { id: 1, name: "Air Conditioner", kWh: 10, time: "5h" },
    { id: 2, name: "Fridge", kWh: 3, time: "24h" },
  ]);

  const addDevice = () => {
    const name = prompt("Device name?");
    const kWh = parseFloat(prompt("Consumption (kWh)?"));
    const time = prompt("Usage time?");
    if (name && !isNaN(kWh) && time)
      setDevices([...devices, { id: Date.now(), name, kWh, time }]);
  };

  const deleteDevice = (id) => setDevices(devices.filter((d) => d.id !== id));

  const editDevice = (id) => {
    const device = devices.find((d) => d.id === id);
    const name = prompt("Device name?", device.name);
    const kWh = parseFloat(prompt("Consumption (kWh)?", device.kWh));
    const time = prompt("Usage time?", device.time);
    if (name && !isNaN(kWh) && time)
      setDevices(
        devices.map((d) => (d.id === id ? { ...d, name, kWh, time } : d))
      );
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>
        Devices <button onClick={addDevice}>+</button>
      </h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: 20 }}>
        {devices.map((device) => (
          <DeviceCard
            key={device.id}
            device={device}
            onDelete={deleteDevice}
            onEdit={editDevice}
          />
        ))}
      </div>
    </div>
  );
}

export default Device;
