import React, { useState } from "react";
import DeviceCard from "./DeviceCard";

function Device() {
  const [devices, setDevices] = useState([
    { id: 1, name: "Air Conditioner", kWh: 10, time: "5h" },
    { id: 2, name: "Fridge", kWh: 3, time: "24h" },
  ]);

  const addDevice = () => {
    const newDevice = {
      id: Date.now(),
      name: "New Device",
      kWh: 0,
      time: "0h",
    };
    setDevices([...devices, newDevice]);
  };

  const deleteDevice = (id) => {
    setDevices(devices.filter((d) => d.id !== id));
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>
        Devices <button onClick={addDevice}>+</button>
      </h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: 20 }}>
        {devices.map((device) => (
          <DeviceCard key={device.id} device={device} onDelete={deleteDevice} />
        ))}
      </div>
    </div>
  );
}

export default Device;
