/* App.js */
import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import "./App.css";

function App() {
const [user, setUser] = useState("admin"); // giả lập login
const [activeMenu, setActiveMenu] = useState("Dashboard");

const [devices, setDevices] = useState([
{
id: 1,
device_id: "D001",
user_id: "U001",
name: "Máy lạnh",
kWh: 12,
time: "10:00 - 12:00",
address: "Phòng khách",
status: "On",
date: "2025-06-01",
color: "#28a745"
},
{
id: 2,
device_id: "D002",
user_id: "U002",
name: "Tivi",
kWh: 5,
time: "18:00 - 20:00",
address: "Phòng ngủ",
status: "Off",
date: "2025-06-02",
color: "#dc3545"
},
]);

const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
};

let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={devices} />;
break;
case "Device":
content = <Device devices={devices} setDevices={setDevices} />;
break;
case "Setting":
content = <Setting />;
break;
default:
content = <Dashboard devices={devices} />;
}

return ( <div className="app-container"> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} /> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
