import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";

import "./App.css";

function App() {
  const [user] = useState({ USER_ID: 300000, USER_NAME: "Admin" });
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Lấy device từ server khi component mount
  useEffect(() => {
    async function fetchDevices() {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices/user/${user.USER_ID}`
        );
        const data = await res.json();
        setDevices(data);
      } catch (err) {
        console.error("❌ Lỗi khi fetch devices:", err);
      }
    }
    fetchDevices();
  }, [user.USER_ID]);

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = (
        <DeviceManager
          user={user}
          onDevicesUpdate={setDevices} // cập nhật devices cho Dashboard
        />
      );
      break;

    case "Setting":
      content = <Setting />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div className="app-container">
      <Sidebar
        activeMenu={activeMenu}
        setActiveMenu={setActiveMenu}
        user={user}
      />
      <div className="main-content">
        <Header user={user} />
        {content}
      </div>
    </div>
  );
}

export default App;
