import React, { useState } from "react";
import DeviceCard from "./DeviceCard";
import styles from "../styles/Device.module.css";

const initialDevices = [
  { id: 1, name: "Fridge", kWh: 2, hours: 24 },
  { id: 2, name: "TV", kWh: 1, hours: 5 },
];

function Device() {
  const [devices, setDevices] = useState(initialDevices);

  const addDevice = () => {
    const name = prompt("Tên thiết bị:");
    const kWh = parseFloat(prompt("Số điện tiêu thụ (kWh):"));
    const hours = parseFloat(prompt("Thời gian sử dụng (giờ):"));
    if (name && !isNaN(kWh) && !isNaN(hours)) {
      setDevices([...devices, { id: Date.now(), name, kWh, hours }]);
    }
  };

  const removeDevice = (id) => {
    setDevices(devices.filter((device) => device.id !== id));
  };

  return (
    <div className={styles.device}>
      <h2>Device Management</h2>
      <button onClick={addDevice}>+</button>
      <div className={styles.deviceList}>
        {devices.map((device) => (
          <DeviceCard key={device.id} device={device} onRemove={removeDevice} />
        ))}
      </div>
    </div>
  );
}

export default Device;
