import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // ================= Load devices + POWER =================
  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices-with-power");
      const devicesWithColor = res.data.map((d) => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
    } catch (err) {
      console.error("Lỗi fetch devices:", err);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, []);

  // ================= Thêm device mới =================
  const handleAdd = async () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    try {
      await axios.post("http://localhost:5000/api/devices", {
        ...form,
        USER_ID: user.USER_ID,
      });

      // reload devices sau khi thêm
      fetchDevices();

      // reset form
      setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
    } catch (err) {
      console.error(err);
      alert("Lưu thiết bị thất bại!");
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      {/* Thêm device */}
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          maxWidth: "400px",
        }}
      >
        <h4>Thêm thiết bị</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Lưu thiết bị
        </button>
      </div>

      {/* Danh sách device */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {(devices || []).map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>POWER: {d.kWh !== undefined ? d.kWh + " W" : "0 W"}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
