import React, { useState } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, onDevicesUpdate, devices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    ADDRESS: addresses[0],
    STATUS: "On",
    CREATE_AT: "",
  });

  const handleAdd = () => {
    if (!form.DEVICE_NAME || !form.kWh || !form.CREATE_AT) {
      return alert("Nhập đủ Tên thiết bị, kWh và Ngày");
    }

    const newDevice = {
      DEVICE_ID: devices.length + 1,
      USER_ID: user.USER_ID,
      DEVICE_NAME: form.DEVICE_NAME,
      ADDRESS: form.ADDRESS,
      STATUS: form.STATUS,
      kWh: Number(form.kWh),
      CREATE_AT: form.CREATE_AT,
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    };

    const updatedDevices = [...devices, newDevice];
    onDevicesUpdate(updatedDevices);

    setForm({
      DEVICE_NAME: "",
      kWh: "",
      ADDRESS: addresses[0],
      STATUS: "On",
      CREATE_AT: "",
    });
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          maxWidth: "400px",
        }}
      >
        {" "}
        <h4>Thêm thiết bị mới</h4>
        <input
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
        />
        <input
          placeholder="kWh"
          value={form.kWh}
          onChange={(e) => setForm({ ...form, kWh: e.target.value })}
        />
        <input
          placeholder="Ngày (YYYY-MM-DD)"
          value={form.CREATE_AT}
          onChange={(e) => setForm({ ...form, CREATE_AT: e.target.value })}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}{" "}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
        >
          {" "}
          <option>On</option> <option>Off</option>{" "}
        </select>{" "}
        <button onClick={handleAdd}>Thêm thiết bị</button>{" "}
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              borderLeft: `4px solid ${d.color}`,
              minWidth: "200px",
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>kWh: {d.kWh}</p>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>Ngày: {d.CREATE_AT}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
