import React, { useState } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, onDevicesUpdate, devices }) {
const [form, setForm] = useState({
DEVICE_NAME: "",
kWh: "",
ADDRESS: addresses[0],
STATUS: "On",
CREATE_AT: "",
});

const handleAdd = () => {
if (!form.DEVICE_NAME || !form.kWh || !form.CREATE_AT) {
return alert("Nhập đủ Tên thiết bị, kWh và Ngày");
}

```
const newDevice = {
  DEVICE_ID: devices.length + 1,
  USER_ID: user.USER_ID,
  DEVICE_NAME: form.DEVICE_NAME,
  ADDRESS: form.ADDRESS,
  STATUS: form.STATUS,
  kWh: Number(form.kWh),
  CREATE_AT: form.CREATE_AT,
  color: "#" + Math.floor(Math.random() * 16777215).toString(16),
};

const updatedDevices = [...devices, newDevice];
onDevicesUpdate(updatedDevices);

setForm({ DEVICE_NAME: "", kWh: "", ADDRESS: addresses[0], STATUS: "On", CREATE_AT: "" });
