import React, { useState } from "react";

function DeviceCard({ device, onDelete, onEdit }) {
  const [hover, setHover] = useState(false);

  return (
    <div
      style={{
        border: "1px solid #ccc",
        padding: 10,
        width: 200,
        position: "relative",
        borderRadius: 4,
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      <h3>{device.name}</h3>
      <p>Consumption: {device.kWh} kWh</p>
      <p>Time: {device.time}</p>
      {hover && (
        <div
          style={{
            position: "absolute",
            top: 5,
            right: 5,
            display: "flex",
            gap: 5,
          }}
        >
          <button onClick={() => onEdit(device.id)}>✏️</button>
          <button onClick={() => onDelete(device.id)}>❌</button>
        </div>
      )}
    </div>
  );
}

export default DeviceCard;
